function showDivs() 
{
var multi, divArray = ["next_button", "previous_button"];
for (multi in divArray)
{
	if (multi) 
	{
		document.getElementById(divArray[multi]).style.display = 'block';
	}
}
}
function hideDivs() 
{
var multi, divArray = ["next_button", "previous_button"];
for (multi in divArray)
{
	if (multi) 
	{
		document.getElementById(divArray[multi]).style.display = 'none';
	}
}
}





